mapper.insert:
INSERT INTO MY_VOTE_ANSWER(
    MY_VOTE_ID, VOTE_ID, USER_ID, CHK
)
VALUES (
           MY_VOTE_ANSWER_SEQ.NEXTVAL, #{VOTE_ID}, #{USER_ID}, #{CHK,jdbcType=VARCHAR }
       )

mapper.update:
UPDATE MY_VOTE_ANSWER
SET
    CHK = #{CHK}
WHERE
    VOTE_ID = #{VOTE_ID}
AND
    USER_ID = #{USER_ID}

mapper.select:
SELECT *
FROM MY_VOTE_ANSWER
WHERE
    VOTE_ID = #{VOTE_ID}
AND
    USER_ID = #{USER_ID}
